package com.syntax.classreview;

public class RecapAsghar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		num=num+1;
		System.out.println(num);
		num++;
		System.out.println(num);
		num+=10;
		System.out.println(num);
		num*=100;
		System.out.println(num);
		num=10;
		++num;
		System.out.println(num);
		num++;
		System.out.println(num);
		
	}

}
