package com.syntax.classreview;

public class ReviewInLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int x=0;
		while(x<2) {
			System.out.println("Hello");
			x=x+1;
		}
		int y=5;
		do {
			System.out.println("hello");
		}while (y<10);
		
		
		
		
	}

}
